import React, { useState } from "react";
import Sidebar from "./Sidebar";
import Table from "./Table";
import Modal from "../Modal/Modal";
import { ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import "./Dashboard.css";

const Dashboard = () => {
  const [students, setStudents] = useState([]);
  const [isModalOpen, setIsModalOpen] = useState(false); // للتحكم في ظهور المودال

  const addStudent = (student) => {
    setStudents([...students, student]); // إضافة الطالب إلى القائمة
  };

  return (
    <div className="dashboard-container">
      <Sidebar onAddStudentClick={() => setIsModalOpen(true)} />
      <main>
        <Table students={students} />
      </main>
      {isModalOpen && <AddStudentModal onClose={() => setIsModalOpen(false)} onAddStudent={addStudent} />}
      <ToastContainer />
    </div>
  );
};

export default Dashboard;
