import React, { useState } from 'react';
import './Insights.css';

const insightsData = [
  {
    title: 'Teachers',
    count: 4,
    maxValue: 4 // الحد الأقصى للعدد
  },
  {
    title: 'Students',
    count: 20,
    maxValue: 20 // الحد الأقصى للعدد
  },
  {
    title: 'Gender of students',
    count: 'Girls: 10, Boys: 10',
    maxValue: 20 // الحد الأقصى
  }
];

function Insights() {
  // استخدام useState لتخزين البيانات بشكل ديناميكي
  const [data, setData] = useState(insightsData);

  // تحديث عدد المعلمين
  const updateTeachers = () => {
    setData((prevData) => {
      const updatedData = [...prevData];
      updatedData[0].count = Math.floor(Math.random() * 11); // تغيير العدد بشكل عشوائي بين 0 و 10
      return updatedData;
    });
  };

  // تحديث عدد الطلاب
  const updateStudents = () => {
    setData((prevData) => {
      const updatedData = [...prevData];
      updatedData[1].count = Math.floor(Math.random() * 31); // تغيير العدد بشكل عشوائي بين 0 و 30
      return updatedData;
    });
  };

  // تحديث عدد الطلاب وفقاً للجنس
  const updateGender = () => {
    setData((prevData) => {
      const updatedData = [...prevData];
      const girls = Math.floor(Math.random() * 21); // تغيير العدد بشكل عشوائي بين 0 و 20
      const boys = 20 - girls; // العدد الثاني يظل يساوي 20
      updatedData[2].count = `Girls: ${girls}, Boys: ${boys}`;
      return updatedData;
    });
  };

  return (
    <div className="insights">
      {data.map((item, index) => {
        const count = typeof item.count === 'string' ? item.count : Number(item.count);
        const progress = (count / item.maxValue) * 100; // حساب النسبة المئوية للتقدم
        return (
          <div className="insight-item" key={index}>
            <h3>{item.title}</h3>
            <h1>{item.count}</h1>
            <div className="progress-bar">
              <span style={{ width: `${progress}%` }}></span>
            </div>
            <button onClick={index === 0 ? updateTeachers : index === 1 ? updateStudents : updateGender}>
              Update
            </button>
          </div>
        );
      })}
    </div>
  );
}

export default Insights;
