import React, { useState } from 'react';
import './dash.css';
import logo from "../image/logo.png";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";


function Dashboard() {
    const [isModalOpen, setIsModalOpen] = useState(false);
  const [students, setStudents] = useState([]);
  const [newStudent, setNewStudent] = useState({ name: "", email: "", phone: "", age: "" });
  const [editingIndex, setEditingIndex] = useState(null);

  const toggleModal = () => setIsModalOpen(!isModalOpen);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewStudent({ ...newStudent, [name]: value });
  };

  const addStudent = () => {
    if (!newStudent.name || !newStudent.email || !newStudent.phone || !newStudent.age) {
      toast.error("Please fill all fields");
      return;
    }
    setStudents([...students, newStudent]);
    setNewStudent({ name: "", email: "", phone: "", age: "" });
    toggleModal();
    toast.success("Student added successfully!");
  };
  // حالة لإدارة عرض القائمة الجانبية
  const [isSideMenuVisible, setSideMenuVisible] = useState(false);

  // حالة لإدارة الوضع الليلي
  const [isDarkMode, setDarkMode] = useState(false);

  // دالة لتبديل عرض القائمة الجانبية
  const toggleSideMenu = () => {
    setSideMenuVisible(!isSideMenuVisible);
  };

  // دالة لتبديل الوضع الليلي
  const toggleTheme = () => {
    setDarkMode(!isDarkMode);
  };

  return (
    <div className={`container ${isDarkMode ? 'dark-theme-variables' : ''}`}>
      <aside style={{ display: isSideMenuVisible ? 'block' : 'none' }}>
        <div className="top">
          <div className="logo1">
            <img src={logo} alt="Logo" />
            <h3>
              <span className="danger"></span>{" "}
            </h3>
          </div>
          {/* <div className="close" id="close_btn" onClick={toggleSideMenu}>
            <span className="material-symbols-sharp">close</span>
          </div> */}
        </div>
        {/* end top */}
        <div className="sidebar">
          {/* <a href="#">
            <span className="material-symbols-sharp">grid_view </span>
            <h3>Dashboard</h3>
          </a> */}
          <a href="#" className="active">
            <span className="material-symbols-sharp">Teachers </span>
          
          </a>
         
          <a href="#" onClick={(e) => { e.preventDefault(); toggleModal(); }} className="add-student-link">Add Student</a>

          {isModalOpen && (
        <div className="modal-overlay" style={{
          position: "fixed",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          backgroundColor: "rgba(0, 0, 0, 0.5)",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          zIndex: 1000
        }}>
          <div className="modal" style={{
            backgroundColor: "white",
            padding: "20px",
            borderRadius: "8px",
            width: "300px",
            textAlign: "center"
          }}>
            
            <input type="text" name="name" placeholder="Name" value={newStudent.name} onChange={handleInputChange} />
            <input type="email" name="email" placeholder="Email" value={newStudent.email} onChange={handleInputChange} />
            <input type="text" name="phone" placeholder="Phone" value={newStudent.phone} onChange={handleInputChange} />
            <input type="number" name="age" placeholder="Age" value={newStudent.age} onChange={handleInputChange} />
            <button onClick={addStudent}>{editingIndex !== null ? "Update" : "Add"}</button>
            <button onClick={toggleModal}>Cancel</button>
          </div>
        </div>
      )}
       
          <a href="#">
            <span className="material-symbols-sharp">logout </span>
           
          </a>
        </div>
      </aside>
      {/* --------------
    end aside
  -------------------- */}
      {/* --------------
    start main part
  --------------- */}
      <main>
        <h1>School Manegment </h1>
        <div className="insights">
          <div className="sales">
            <div className="middle">
              <div className="left">
                <h3>Teachers</h3>
                <h1>4</h1>
              </div>
              <div className="progress">
                {/* <svg>
                  <circle  cy={40} cx={40} />
                </svg> */}
                {/* <div className="number">
                  <p>80%</p>
                </div> */}
              </div>
            </div>
          
          </div>
          {/* end selling */}
          {/* start expenses */}
          <div className="expenses">
            <div className="middle">
              <div className="left">
                <h3>Students</h3>
                <h1>20</h1>
              </div>
              <div className="progress">
              </div>
            </div>
           
          </div>
          <div className="income">
            <div className="middle">
              <div className="left">
                <h3>Gender of student</h3>
                <h1>girls: 10</h1>
                <h1>boys: 10</h1>
              </div>
              <div className="progress">
                <div className="number">
                </div>
              </div>
            </div>
          </div>
          {/* end income */}
        </div>
        {/* end insights */}
        <div className="recent_order">
          <h2>scduale of students</h2>
          <table>
            <thead>
              <tr>
                <th>student Name</th>
                <th>Email</th>
                <th>Phone</th>
                <th>age</th>
              </tr>
            </thead>
            <tbody>
            {students.map((student, index) => (
              <tr key={index}>
                <td>{student.name}</td>
                <td>{student.email}</td>
                <td>{student.phone}</td>
                <td>{student.age}</td>
              </tr>
            ))}
          </tbody>
          </table>
          <ToastContainer />
          <a href="#">Show All</a>
        </div>
      </main>
      {/*----------------
     end main
    -----------------*/}
      {/*--------------
    start right main 
  --------------------*/}
      <div className="right">
        <div className="top">
          <button id="menu_bar" onClick={toggleSideMenu}>
            <span className="material-symbols-sharp">menu</span>
          </button>
          <div className="theme-toggler" onClick={toggleTheme}>
            <span className={`material-symbols-sharp ${!isDarkMode ? 'active' : ''}`}>light_mode</span>
            <span className={`material-symbols-sharp ${isDarkMode ? 'active' : ''}`}>dark_mode</span>
          </div>
          <div className="profile">
            <div className="info">
              <p>
                <b>Babar</b>
              </p>
              <p>Admin</p>
              <small className="text-muted" />
            </div>
            <div className="profile-photo">
              <img src="./images/profile-1.jpg" alt="" />
            </div>
          </div>
        </div>
       
      </div>
    </div>
  );
}

export default Dashboard;
