import {
    Chart as ChartJS,
    CategoryScale,
    LinearScale,
    BarElement,
    Title,
    Tooltip,
    Legend,
  } from "chart.js";
  import { useEffect, useRef } from "react";
  import Chart from "chart.js/auto";
  
  ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);
  
  const ChartComponent = ({ data }) => {
    const chartRef = useRef(null);
    const chartInstance = useRef(null);
  
    useEffect(() => {
      if (chartInstance.current) {
        chartInstance.current.destroy(); // üõë ÿ™ÿØŸÖŸäÿ± ÿßŸÑŸÖÿÆÿ∑ÿ∑ ÿßŸÑÿ≥ÿßÿ®ŸÇ ŸÇÿ®ŸÑ ÿ•ŸÜÿ¥ÿßÿ° ÿ¨ÿØŸäÿØ
      }
  
      const ctx = chartRef.current.getContext("2d");
      chartInstance.current = new Chart(ctx, {
        type: "bar",
        data: data,
        options: {
          responsive: true,
          plugins: {
            legend: { display: true },
            title: { display: true, text: "My Chart" },
          },
        },
      });
  
      return () => {
        if (chartInstance.current) {
          chartInstance.current.destroy(); // üõë ÿ™ŸÜÿ∏ŸäŸÅ ÿπŸÜÿØ ÿ™ŸÅŸÉŸäŸÉ ÿßŸÑŸÖŸÉŸàŸÜ
        }
      };
    }, [data]);
  
    return <canvas ref={chartRef} />;
  };
  
  export default ChartComponent;
