import React from "react";
import { Formik, Form, Field } from "formik";
import * as Yup from "yup";

const Teachers = () => {
  return (
    <div>
      <h2 className="text-xl font-bold mb-4">Teacher Form</h2>
      <Formik
        initialValues={{ name: "", subject: "" }}
        validationSchema={Yup.object({
          name: Yup.string().required("Required"),
          subject: Yup.string().required("Required"),
        })}
        onSubmit={(values) => {
          alert(JSON.stringify(values, null, 2));
        }}
      >
        {({ errors, touched }) => (
          <Form className="space-y-4">
            <Field name="name" placeholder="Teacher Name" className="border p-2 w-full" />
            {errors.name && touched.name && <div className="text-red-500">{errors.name}</div>}

            <Field name="subject" placeholder="Subject" className="border p-2 w-full" />
            {errors.subject && touched.subject && <div className="text-red-500">{errors.subject}</div>}

            <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded">Submit</button>
          </Form>
        )}
      </Formik>
    </div>
  );
};

export default Teachers;
