import React from 'react';
import { Line } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend } from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

const AgeChart = () => {
  // مصفوفة الطلاب داخل المكون
  const students = [
    { name: "Ahmed", age: 5 },
    { name: "Sara", age: 6 },
    { name: "Omar", age: 7 },
    { name: "Laila", age: 6 },
    { name: "Ali", age: 8 },
    { name: "Noor", age: 5 },
    { name: "Maha", age: 9 },
    { name: "Khaled", age: 7 },
    { name: "Yara", age: 10 },
    { name: "Amir", age: 6 },
    { name: "Layla", age: 8 },
    { name: "Faisal", age: 9 },
    { name: "Hassan", age: 7 },
    { name: "Reem", age: 6 },
    { name: "Zain", age: 10 },
    { name: "Jana", age: 5 },
    { name: "Tariq", age: 8 },
    { name: "Mariam", age: 9 },
    { name: "Salim", age: 7 },
    { name: "Dina", age: 6 }
  ];

  // أسماء الطلاب
  const studentNames = students.map(student => student.name);

  // إعدادات البيانات لمخطط الأعمار
  const ageData = {
    labels: studentNames, // تسميات الطلاب
    datasets: [
      {
        label: 'Age of Students',
        data: students.map(student => student.age), // بيانات الأعمار
        fill: false,
        borderColor: '#5d4037', // لون خط المخطط البني
        tension: 0.1
      }
    ]
  };

  // إعدادات المخطط
  const options = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top'
      },
      title: {
        display: true,
        text: 'Age Distribution of 20 Students'
      }
    }
  };

  return (
    <div className="insight-item">
      <h3>Age Distribution of 20 Students</h3>
      <Line data={ageData} options={options} />
    </div>
  );
};

export default AgeChart;
