import React, { useState } from 'react';
import './sidebardash.css';
import logo from "../logo.png";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

function Sidebar({ isSideMenuVisible, toggleSideMenu }) {
      const [isModalOpen, setIsModalOpen] = useState(false);
  const [students, setStudents] = useState([]);
  const [newStudent, setNewStudent] = useState({ name: "", email: "", phone: "", age: "" });
  const [editingIndex, setEditingIndex] = useState(null);

  const toggleModal = () => setIsModalOpen(!isModalOpen);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewStudent({ ...newStudent, [name]: value });
  };

  const addStudent = () => {
    if (!newStudent.name || !newStudent.email || !newStudent.phone || !newStudent.age) {
      toast.error("Please fill all fields");
      return;
    }
    setStudents([...students, newStudent]);
    setNewStudent({ name: "", email: "", phone: "", age: "" });
    toggleModal();
    toast.success("Student added successfully!");
  };
  return (

    <aside style={{ display: isSideMenuVisible ? 'block' : 'none' }}>
      <div className="logo1">
        <img src={logo} alt="Logo" />
        <h3><span className="danger"></span></h3>
      </div>
      <div className="sidebar">
        <a href="#" className="active">
          <span className="material-symbols-sharp">Teachers</span>
        </a>
        <a href="#" className="add-student-link" onClick={(e) => { e.preventDefault(); toggleSideMenu(); }}>
          Add Student
        </a>
      </div>
      //           {isModalOpen && (
        <div className="modal-overlay" style={{
          position: "fixed",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          backgroundColor: "rgba(0, 0, 0, 0.5)",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          zIndex: 1000
        }}>
          <div className="modal" style={{
            backgroundColor: "white",
            padding: "20px",
            borderRadius: "8px",
            width: "300px",
            textAlign: "center"
          }}>
            
            <input type="text" name="name" placeholder="Name" value={newStudent.name} onChange={handleInputChange} />
            <input type="email" name="email" placeholder="Email" value={newStudent.email} onChange={handleInputChange} />
            <input type="text" name="phone" placeholder="Phone" value={newStudent.phone} onChange={handleInputChange} />
            <input type="number" name="age" placeholder="Age" value={newStudent.age} onChange={handleInputChange} />
            <button onClick={addStudent}>{editingIndex !== null ? "Update" : "Add"}</button>
            <button onClick={toggleModal}>Cancel</button>
          </div>
        </div>
      )}
    </aside>
  );
}

export default Sidebar;
