import React from 'react';
import { Bar } from 'react-chartjs-2';  // تأكد من أن هذا السطر موجود
import { Chart as ChartJS, ArcElement, Tooltip, Legend, CategoryScale, LinearScale, BarElement } from 'chart.js';

ChartJS.register(ArcElement, Tooltip, Legend, CategoryScale, LinearScale, BarElement);

const Charts = ({ students = [] }) => {
  if (!students.length) {
    return <div>No data available to display.</div>;
  }

  const ageGroups = [5, 6, 7, 8, 9, 10];
  const ageCounts = ageGroups.map(age => students.filter(student => student.age === age).length);

  const ageData = {
    labels: ageGroups,
    datasets: [
      {
        label: 'Number of Students',
        data: ageCounts,
        backgroundColor: '#5d4037',
      }
    ]
  };

  const options = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top'
      },
      title: {
        display: true,
        text: 'Number of Students in Each Age Group'
      }
    },
    scales: {
      x: {
        title: {
          display: true,
          text: 'Age Groups'
        }
      },
      y: {
        title: {
          display: true,
          text: 'Number of Students'
        },
        beginAtZero: true
      }
    }
  };

  return (
    <div className="insight-item">
      <h3>Number of Students in Each Age Group</h3>
      <Bar data={ageData} options={options} />
    </div>
  );
};

export default Charts;
