import React from 'react';
import { Line } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend } from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

const AgeChart = ({ ages }) => {
  // أسماء الطلاب
  const studentNames = Array.from({ length: 20 }, (_, index) => `Student ${index + 1}`);

  // إعدادات البيانات لمخطط الأعمار
  const ageData = {
    labels: studentNames, // تسميات الطلاب
    datasets: [
      {
        label: 'Age of Students',
        data: ages, // بيانات الأعمار
        fill: false,
        borderColor: '#5d4037', // لون خط المخطط البني
        tension: 0.1
      }
    ]
  };

  // إعدادات المخطط
  const options = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top'
      },
      title: {
        display: true,
        text: 'Age Distribution of 20 Students'
      }
    }
  };

  return (
    <div className="insight-item">
      <h3>Age Distribution of 20 Students</h3>
      <Line data={ageData} options={options} />
    </div>
  );
};

export default AgeChart;
