import { useState } from "react";

export default function Add() {
  const [number, setnumber] = useState(0);

  const pls = () => {
    setnumber(number + 1);
  };
  const mas = () => {
    setnumber(number - 1);
  };

  return (
    <div>
      <button onClick={pls}>+</button>
      <button onClick={mas}>-</button>
      <h3>{number}</h3>
    </div>
  );
}
